function pilih_kategori(kategori){
    $.ajax({
          type:"post",
          url: "<?php echo base_url('adminstand/getProdukInKategori')?>/",
          data:{ kategori:kategori},
          dataType:"json",
          success:function(response)
          {
            var kirikanan = 1;
            document.getElementById("menusectionleft").innerHTML = "";
            document.getElementById("menusectionright").innerHTML = "";
            for(var i=0;i< response.length; i++){
                var div = document.createElement('div');
                div.className = "menu col-lg-12 offset-lg-1 col-md-12 offset-md-1";
                div.setAttribute("onclick", "pilih_topping('"+kategori+"','"+response[i].nama_produk+"','"+response[i].harga_jual+"','"+response[i].id_produk+"')");
                div.innerHTML = response[i].nama_produk;
                if (kirikanan%2 == 0) {
                    document.getElementById('menusectionright').appendChild(div);
                }else{
                    
                    document.getElementById('menusectionleft').appendChild(div);
                }
                kirikanan++;
                
            }
          },
      }
    );
}

function autobtn(){
    $("#total_bayar").text(currency(total_harus_byr));
    hitungKembalian();
}

function hitungKembalian(){

    totalygdibayar = parseInt($("#total_bayar").html().split('.').join(""));
    selisih = totalygdibayar-total_harus_byr;
    if (selisih>=0) {
        $("#kembalian").text("Rp "+currency(total_harus_byr-totalygdibayar));
        if ($('#buttonkembali').html() != "Selesai") {
            $('#buttoncetaknota').prop("disabled", false);
        }else{
            $('#buttoncetaknota').prop("disabled", true);
        }
    }else{
        $("#kembalian").text("- Rp "+currency(total_harus_byr-totalygdibayar));
        $('#buttoncetaknota').prop("disabled", true);
        
        
    }
    // alert(selisih);
}

function selectTopping(id){
    if(id.childNodes[0].classList.contains('activetopping')){
        id.childNodes[0].classList.remove('activetopping');
    }else{
        id.childNodes[0].classList.add('activetopping');
    }
}

